    <svg viewBox="0 0 24 24" fill="none" aria-hidden="true" {...props}>
      <path d="M16 20c0-2.2-1.8-4-4-4s-4 1.8-4 4" stroke="currentColor" strokeWidth="1.7" strokeLinecap="round" />
      <path d="M12 13a4 4 0 1 0 0-8 4 4 0 0 0 0 8Z" stroke="currentColor" strokeWidth="1.7" />
      <path d="M20 20c0-1.75-1.25-3.23-2.9-3.67" stroke="currentColor" strokeWidth="1.7" strokeLinecap="round" />
      <path d="M17.2 5.7a3.3 3.3 0 0 1 0 6.6" stroke="currentColor" strokeWidth="1.7" strokeLinecap="round" />
    </svg>
  )
}

function IconChain(props) {
  return (
    <svg viewBox="0 0 24 24" fill="none" aria-hidden="true" {...props}>
      <path d="M10 13a5 5 0 0 0 7.54.54l3-3a5 5 0 1 0-7.07-7.07l-1.72 1.71" stroke="currentColor" strokeWidth="1.7" strokeLinecap="round" />
      <path d="M14 11a5 5 0 0 0-7.54-.54l-3 3a5 5 0 1 0 7.07 7.07l1.71-1.71" stroke="currentColor" strokeWidth="1.7" strokeLinecap="round" />
    </svg>
  )
}

function IconShield(props) {
  return (
    <svg viewBox="0 0 24 24" fill="none" aria-hidden="true" {...props}>
      <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z" stroke="currentColor" strokeWidth="1.7" strokeLinejoin="round" />
    </svg>
  )
}

function IconBuilding(props) {
  return (
    <svg viewBox="0 0 24 24" fill="none" aria-hidden="true" {...props}>
      <rect x="4" y="2" width="16" height="20" rx="2" stroke="currentColor" strokeWidth="1.7" />
      <path d="M9 6h2M9 10h2M9 14h2M13 6h2M13 10h2M13 14h2M9 19v3h6v-3" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" />
    </svg>
  )
}

function IconUserCircle(props) {
  return (
    <svg viewBox="0 0 24 24" fill="none" aria-hidden="true" {...props}>
      <path d="M12 22c5.523 0 10-4.477 10-10S17.523 2 12 2 2 6.477 2 12s4.477 10 10 10Z" stroke="currentColor" strokeWidth="1.5" />
      <path d="M16.5 18.5c-.65-2.08-2.75-3.5-4.5-3.5s-3.85 1.42-4.5 3.5" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" />
      <path d="M12 13a3 3 0 1 0 0-6 3 3 0 0 0 0 6Z" stroke="currentColor" strokeWidth="1.5" />
    </svg>
  )
}

function NavItem({ to, icon: Icon, label, onNavigate }) {
  return (
    <NavLink
      to={to}
      className={({ isActive }) => `sp-navItem${isActive ? ' active' : ''}`}
      end
      onClick={onNavigate}
    >
      <Icon className="sp-navIcon" />
      <span className="sp-navLabel">{label}</span>
    </NavLink>
  )
}

export default function Sidebar({ isOpen, onRequestClose, onNavigate, onAccountSettings }) {
  const { user, logout } = useAuthStore()
  const isAdmin = user?.role === 'ADMIN'

  const handleLogout = () => {
    logout()
    window.location.href = '/login'
  }

  return (
    <aside className={`sp-sidebar${isOpen ? ' open' : ''}`}>
      <div className="sp-brand">
        <button className="sp-brandAction" type="button" onClick={onAccountSettings} aria-label="Pengaturan akun">
          <IconUserCircle className="sp-brandAvatar" />
        </button>
        <div className="sp-brandMeta">
          <div className="sp-brandTitle">SmartPoultry</div>
          <div className="sp-brandCaption">Processor ‚Ä¢ {user?.role || 'User'}</div>
        </div>
      </div>

      <nav className="sp-nav">
        <NavItem to="/app/dashboard" icon={IconDashboard} label="Dashboard" onNavigate={onNavigate} />
        {isAdmin && (
          <NavItem to="/app/orders" icon={IconOrder} label="Order" onNavigate={onNavigate} />
        )}
        {isAdmin && (
          <NavItem to="/app/nota-penerimaan" icon={IconReceipt} label="Nota Penerimaan" onNavigate={onNavigate} />
        )}
        <NavItem to="/app/tugas" icon={IconTask} label="Tugas Produksi" onNavigate={onNavigate} />
        <NavItem to="/app/produksi" icon={IconFactory} label="Monitoring Produksi" onNavigate={onNavigate} />
        <NavItem to="/app/laporan" icon={IconChart} label="Laporan Produksi" onNavigate={onNavigate} />
        {isAdmin && (
          <NavItem to="/app/pengiriman" icon={IconTruck} label="Pengiriman" onNavigate={onNavigate} />
        )}
        {isAdmin && (
          <NavItem to="/app/nota" icon={IconReceipt} label="Nota Pengiriman" onNavigate={onNavigate} />
        )}
        {isAdmin && (
          <NavItem to="/app/karyawan" icon={IconUsers} label="Karyawan" onNavigate={onNavigate} />
        )}
        {isAdmin && (
          <NavItem to="/app/blockchain" icon={IconChain} label="Blockchain" onNavigate={onNavigate} />
        )}

        {/* Admin Section Divider */}
        {isAdmin && (
          <>
            <div style={{
              margin: '12px 14px 8px', paddingTop: 12,
              borderTop: '1px solid rgba(139,26,26,0.10)',
              fontSize: '0.68rem', fontWeight: 700, letterSpacing: '0.08em',
              textTransform: 'uppercase', color: 'var(--muted)', opacity: 0.6,
            }}>
              Admin Panel
            </div>
            <NavItem to="/app/admin/processors" icon={IconBuilding} label="Panel Processor" onNavigate={onNavigate} />
            <NavItem to="/app/admin/blockchain" icon={IconShield} label="Admin Blockchain" onNavigate={onNavigate} />
          </>
        )}
      </nav>

      <div className="sp-sidebarFooter">
        <button className="sp-btn danger" type="button" onClick={handleLogout}>Logout</button>
      </div>
    </aside>
  )
}

import { createContext, useContext, useState, useCallback } from 'react'

const ToastContext = createContext()

export function useToast() {
  return useContext(ToastContext)
}

let toastId = 0

export function ToastProvider({ children }) {
  const [toasts, setToasts] = useState([])

  const showToast = useCallback(({ title, description, status = 'info', duration = 4000 }) => {
    const id = ++toastId
    setToasts((prev) => [...prev, { id, title, description, status }])
    setTimeout(() => {
      setToasts((prev) => prev.filter((t) => t.id !== id))
    }, duration)
  }, [])

  const removeToast = useCallback((id) => {
    setToasts((prev) => prev.filter((t) => t.id !== id))
  }, [])

  return (
    <ToastContext.Provider value={{ showToast }}>
      {children}
      <div className="sp-toastContainer">
        {toasts.map((t) => (
          <div key={t.id} className={`sp-toast ${t.status}`}>
            <div>
              <div className="sp-toastTitle">{t.title}</div>
              {t.description && <div className="sp-toastDesc">{t.description}</div>}
            </div>
            <button className="sp-toastClose" onClick={() => removeToast(t.id)}>&times;</button>
          </div>
        ))}
      </div>
    </ToastContext.Provider>
  )
}

import { useState } from 'react'
import { useToast } from '../components/ToastProvider'
import useAuthStore from '../stores/authStore'
import apiClient from '../services/apiClient'

export default function AccountSettings() {
  const { user, updateUser } = useAuthStore()
  const { showToast } = useToast()
  const [name, setName] = useState(user?.nama || '')
  const [loading, setLoading] = useState(false)

  const handleSave = async (e) => {
    e.preventDefault()
    setLoading(true)
    try {
      const res = await apiClient.put('/auth/me', { namaProcessor: name })
      updateUser({ ...user, nama: name })
      showToast({ title: 'Berhasil', description: 'Profil diperbarui.', status: 'success' })
    } catch (err) {
      showToast({ title: 'Gagal', description: err.response?.data?.message || 'Gagal memperbarui.', status: 'error' })
    } finally {
      setLoading(false)
    }
  }

  const initials = (user?.nama || 'U').slice(0, 2).toUpperCase()

  return (
    <div style={{ maxWidth: 900, margin: '0 auto', paddingBottom: 60 }}>
      {/* Page Header */}
      <div style={{
        display: 'flex', alignItems: 'center', justifyContent: 'space-between',
        gap: 20, flexWrap: 'wrap', marginBottom: 32, padding: '28px 32px',
        background: 'linear-gradient(135deg, #3C1111 0%, #5C1A1A 50%, #8B1A1A 100%)',
        borderRadius: 20, color: '#FFF5F2',
        boxShadow: '0 4px 12px rgba(139,26,26,0.12), 0 16px 40px rgba(44,24,16,0.1)',
        position: 'relative', overflow: 'hidden',
      }}>
        <div style={{ position: 'absolute', top: -50, right: -30, width: 160, height: 160, borderRadius: '50%', background: 'rgba(255,255,255,0.03)' }} />

        <div style={{ display: 'flex', alignItems: 'center', gap: 20, position: 'relative', zIndex: 1 }}>
          <div style={{
            width: 52, height: 52, borderRadius: 14,
            background: 'rgba(255,245,242,0.12)',
            display: 'flex', alignItems: 'center', justifyContent: 'center',
            fontSize: '1.1rem', fontWeight: 800, letterSpacing: '0.05em',
            color: '#FFF5F2', border: '1.5px solid rgba(255,245,242,0.15)',
          }}>
            {initials}
          </div>
          <div>
            <h1 style={{ fontSize: '1.4rem', fontWeight: 800, margin: '0 0 4px', letterSpacing: '-0.01em' }}>
              Pengaturan Akun
            </h1>
            <p style={{ margin: 0, fontSize: '0.88rem', color: 'rgba(255,245,242,0.6)' }}>
              Kelola informasi akun Anda
            </p>
          </div>
        </div>
        <div style={{
          display: 'flex', alignItems: 'center', gap: 6,
          padding: '5px 14px', background: 'rgba(255,245,242,0.1)',
          borderRadius: 999, fontSize: '0.75rem', fontWeight: 600,
          color: 'rgba(255,245,242,0.7)', border: '1px solid rgba(255,245,242,0.08)',
          position: 'relative', zIndex: 1,
        }}>
          ‚óè {user?.role || 'User'}
        </div>
      </div>

      {/* Content Grid */}
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 20 }}>
        {/* Account Info Card */}
        <div className="sp-card" style={{ gridColumn: '1 / -1' }}>
          <div className="sp-cardHeader">Informasi Akun</div>
          <div className="sp-cardBody">
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))',
              gap: 14,
            }}>
              {[
                { label: 'Email', value: user?.email || '-' },
                { label: 'Role', value: user?.role || '-' },
                { label: 'Status', value: null, isStatus: true },
              ].map((item, i) => (
                <div key={i} style={{
                  padding: '14px 18px', borderRadius: 12,
                  background: '#FAF7F4', border: '1px solid rgba(139,26,26,0.04)',
                }}>
                  <div style={{ fontSize: '0.72rem', fontWeight: 600, textTransform: 'uppercase', letterSpacing: '0.08em', color: '#7A6B63' }}>
                    {item.label}
                  </div>
                  {item.isStatus ? (
                    <div style={{
                      display: 'inline-flex', alignItems: 'center', gap: 6,
                      fontSize: '0.88rem', fontWeight: 600, color: '#16a34a', marginTop: 6,
                    }}>
                      <span style={{
                        width: 7, height: 7, borderRadius: '50%', background: '#16a34a',
                        boxShadow: '0 0 8px rgba(22,163,74,0.5)', animation: 'statusPulse 2s ease-in-out infinite',
                      }} />
                      Aktif
                    </div>
                  ) : (
                    <div style={{ fontSize: '0.95rem', fontWeight: 700, marginTop: 6, color: '#2C1810' }}>
                      {item.value}
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Edit Profile Card */}
        <div className="sp-card">
          <div className="sp-cardHeader">Edit Profil</div>
          <div className="sp-cardBody">
            <form className="sp-form" onSubmit={handleSave}>
              <label className="sp-field">
                <span className="sp-label">Email</span>
                <input className="sp-input" value={user?.email || ''} disabled />
              </label>
              <label className="sp-field">
                <span className="sp-label">Nama Processor</span>
                <input className="sp-input" value={name} onChange={(e) => setName(e.target.value)} />
              </label>
              <label className="sp-field">
                <span className="sp-label">Role</span>
                <input className="sp-input" value={user?.role || ''} disabled />
              </label>
              <div style={{ display: 'flex', justifyContent: 'flex-end', paddingTop: 4 }}>
                <button className="sp-btn" type="submit" disabled={loading}>
                  {loading ? 'Menyimpan...' : 'Simpan Perubahan'}
                </button>
              </div>
            </form>
          </div>
        </div>

        {/* Security Card */}
        <div className="sp-card">
          <div className="sp-cardHeader">Keamanan</div>
          <div className="sp-cardBody">
            <p style={{ color: '#7A6B63', fontSize: '0.9rem', lineHeight: 1.7 }}>
              Untuk mengubah password Anda, silakan hubungi administrator sistem.
              Password yang kuat harus memiliki kombinasi huruf besar, huruf kecil, angka, dan simbol.
            </p>
          </div>
        </div>
      </div>
    </div>
  )
}

import { useState, useEffect, useCallback } from 'react'
import adminService from '../services/adminService'
import { useToast } from '../components/ToastProvider'
import '../styles/AdminDashboard.css'

// Block type configuration for Processor
const BLOCK_TYPE_CONFIG = {
    RECEIVE_FROM_FARM: { label: 'Penerimaan dari Farm', color: '#2563eb', icon: 'üì¶' },
    NOTA_PENERIMAAN: { label: 'Nota Penerimaan', color: '#7c3aed', icon: 'üìã' },
    PROCESSING: { label: 'Produksi', color: '#d97706', icon: 'üè≠' },
    HALAL_CHECK: { label: 'Sertifikat Halal', color: '#059669', icon: '‚ò™Ô∏è' },
    QUALITY_CHECK: { label: 'Quality Control', color: '#0891b2', icon: '‚úÖ' },
    LAPORAN_MASALAH: { label: 'Laporan Masalah', color: '#dc2626', icon: '‚ö†Ô∏è' },
    TRANSFER_TO_RETAIL: { label: 'Kirim ke Retail', color: '#16a34a', icon: 'üöö' },
}

const STATUS_CHAIN_CONFIG = {
    ACTIVE: { label: 'Active', color: '#10b981', bg: 'rgba(16,185,129,0.12)' },
    COMPLETED: { label: 'Completed', color: '#3b82f6', bg: 'rgba(59,130,246,0.12)' },
    FAILED: { label: 'Failed', color: '#ef4444', bg: 'rgba(239,68,68,0.12)' },
    TRANSFERRED: { label: 'Transferred', color: '#8b5cf6', bg: 'rgba(139,92,246,0.12)' },
}

function AdminPanelBlockchain() {
    const [overview, setOverview] = useState(null)
    const [chains, setChains] = useState([])
    const [selectedChainId, setSelectedChainId] = useState(null)
    const [blocks, setBlocks] = useState([])
    const [selectedBlock, setSelectedBlock] = useState(null)
    const [validation, setValidation] = useState(null)
    const [loading, setLoading] = useState({ overview: true, blocks: false })
    const [view, setView] = useState('overview')
    const [searchQuery, setSearchQuery] = useState('')
    const { showToast } = useToast()

    // Load overview data
    const loadOverview = useCallback(async () => {
        setLoading(prev => ({ ...prev, overview: true }))
        try {
            const data = await adminService.getBlockchainOverview(searchQuery)
            setOverview(data)
            setChains(data.chains || [])
        } catch (error) {
            showToast({ title: 'Error', description: 'Gagal memuat data blockchain', status: 'error' })
        } finally {
            setLoading(prev => ({ ...prev, overview: false }))
        }
    }, [searchQuery])

    useEffect(() => {
        const timer = setTimeout(() => {
            loadOverview()
        }, 300)
        return () => clearTimeout(timer)
    }, [loadOverview])

    // Load blocks for a chain
    const loadBlocks = async (identityId) => {
        setLoading(prev => ({ ...prev, blocks: true }))
        try {
            const data = await adminService.getBlocks(identityId)
            setBlocks(data)
            setSelectedBlock(null)
            setValidation(null)
        } catch (error) {
            showToast({ title: 'Error', description: 'Gagal memuat blocks', status: 'error' })
        } finally {
            setLoading(prev => ({ ...prev, blocks: false }))
        }
    }

    const handleSelectChain = (identityId) => {
        setSelectedChainId(identityId)
        setView('chain-detail')
        loadBlocks(identityId)
    }

    const handleValidate = async () => {
        if (!selectedChainId) return
        try {
            const result = await adminService.validateChain(selectedChainId)
            setValidation(result)
            if (result.valid) {
                showToast({ title: '‚úì Valid', description: 'Chain valid! Integritas terjaga.', status: 'success' })
            } else {
                showToast({ title: 'Invalid', description: `Chain tidak valid: ${result.message}`, status: 'error' })
            }
        } catch (error) {
            showToast({ title: 'Error', description: 'Gagal memvalidasi chain', status: 'error' })
        }
    }

    const handleBack = () => {
        setView('overview')
        setSelectedChainId(null)
        setBlocks([])
        setSelectedBlock(null)
        setValidation(null)
    }

    const currentChain = chains.find(c => c.IdIdentity === selectedChainId || c.IdIdentity === parseInt(selectedChainId))

    const formatDate = (dateStr) => {
        if (!dateStr) return '-'
        try {
            return new Date(dateStr).toLocaleDateString('id-ID', { day: '2-digit', month: 'short', year: 'numeric' })
        } catch { return dateStr }
    }

    const formatDateTime = (dateStr) => {
        if (!dateStr) return '-'
        try {
            return new Date(dateStr).toLocaleString('id-ID', { day: '2-digit', month: 'short', year: 'numeric', hour: '2-digit', minute: '2-digit' })
        } catch { return dateStr }
    }

    const truncHash = (hash) => {
        if (!hash) return '...'
        return `${hash.substring(0, 8)}...${hash.substring(hash.length - 8)}`
    }

    return (
        <div className="admin-page">
            {/* Page Header */}
            <div className="admin-page-header">
                <div className="admin-header-content">
                    <div className="admin-header-icon">
                        <svg width="28" height="28" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                            <rect x="1" y="4" width="6" height="6" rx="1" />
                            <rect x="9" y="4" width="6" height="6" rx="1" />
                            <rect x="17" y="4" width="6" height="6" rx="1" />
                            <rect x="5" y="14" width="6" height="6" rx="1" />
                            <rect x="13" y="14" width="6" height="6" rx="1" />
